    (function () {
      var slider1 = document.querySelector('.js-okko-slider1');
      slider1.appendChild(slider1.firstElementChild.cloneNode(slider1.firstElementChild));
      var slides = Array.prototype.slice.call(slider1.querySelectorAll('.js-okko-slide-item'));
      var showTime = 5000;
      var counter = 0;
      var animationStep = 0;
      function animateSlider1() {
        counter += 1;
        animationStep = counter % slides.length;
        if (animationStep === 0) {
          slider1.classList.remove('_with-transition');
          slider1.style.transform = 'translate3d(0, 0, 0)';
          setTimeout(animateSlider1, 0);
          return;
        }
        slider1.style.transform = "translate(0, -" + animationStep * slides[0].offsetHeight + "px)";
        slider1.classList.add('_with-transition');
      }
      setInterval(animateSlider1, showTime);
    })();


