        var inputError = input.parentNode.querySelector('.js-user-news-error');
        if (emailRegexp.test(input.value) || input.value.split('').filter(Number).join('').length >= 10) {
          return true;
        } else if (input.value === '') {
          inputError.textContent = 'Обязательное поле для ввода';
